import React, { useState } from 'react';
import {
  Calendar as CalendarIcon,
  Clock,
  CheckCircle,
  XCircle,
  Users
} from 'lucide-react';
import Calendar from '../components/Attendance/Calendar';
import { format, subDays } from 'date-fns';

const Attendance: React.FC = () => {
  const [selectedDate, setSelectedDate] = useState(new Date('2025-06-30'));

  const mockAttendanceData: { [key: string]: 'present' | 'absent' | 'late' | 'half-day' } = {
    '2025-06-03': 'late',
    '2025-06-13': 'late',
    '2025-06-20': 'late',
    '2025-06-05': 'absent',
    '2025-06-18': 'absent',
    // Present days
    ...Array.from({ length: 30 }, (_, i) => `2025-06-${String(i + 1).padStart(2, '0')}`)
      .filter((d) => !['2025-06-03', '2025-06-05', '2025-06-13', '2025-06-18', '2025-06-20'].includes(d))
      .reduce((acc, day) => ({ ...acc, [day]: 'present' }), {}),
  };

  const handleDateChange = (date: Date) => {
    setSelectedDate(date);
  };

  const attendanceStats = Object.values(mockAttendanceData).reduce(
    (acc, status) => {
      acc[status]++;
      return acc;
    },
    { present: 0, absent: 0, late: 0, halfDay: 0 }
  );

  const totalDays = Object.keys(mockAttendanceData).length;
  const attendancePercentage = Math.round((attendanceStats.present / totalDays) * 100);

  return (
    <div className="space-y-6">
      {/* Header */}
      <div>
        <h1 className="text-2xl font-bold text-gray-900">Attendance</h1>
        <p className="mt-1 text-sm text-gray-500">Track and manage employee attendance records.</p>
      </div>

      {/* Stats */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4">
        <StatCard icon={CheckCircle} label="Present" value={attendanceStats.present} color="green" />
        <StatCard icon={XCircle} label="Absent" value={attendanceStats.absent} color="red" />
        <StatCard icon={Clock} label="Late" value={attendanceStats.late} color="yellow" />
        <StatCard icon={Users} label="Attendance %" value={`${attendancePercentage}%`} color="blue" />
      </div>

      {/* Calendar + Weekly Info */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <Calendar
          currentDate={selectedDate}
          onDateChange={handleDateChange}
          attendanceData={mockAttendanceData}
        />

        <div className="bg-white shadow rounded-lg p-4 sm:p-6 overflow-x-auto">
          <h3 className="text-lg font-medium text-gray-900 mb-4">Details for This Week</h3>
          <table className="min-w-full divide-y divide-gray-200 text-sm">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-4 py-2 text-left text-gray-500 font-medium uppercase">Date</th>
                <th className="px-4 py-2 text-left text-gray-500 font-medium uppercase">Status</th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y">
              {Array.from({ length: 7 })
                .map((_, i) => subDays(selectedDate, 6 - i))
                .filter(date => ![0, 6].includes(date.getDay())) // Exclude Sundays/Saturdays
                .map(date => {
                  const key = format(date, 'yyyy-MM-dd');
                  const status = mockAttendanceData[key] || 'absent';
                  const statusColor = {
                    present: 'green',
                    late: 'yellow',
                    absent: 'red',
                    'half-day': 'purple'
                  }[status];

                  return (
                    <tr key={key}>
                      <td className="px-4 py-2 whitespace-nowrap">{format(date, 'MMM dd, yyyy')}</td>
                      <td className="px-4 py-2">
                        <span className={`inline-flex px-2 py-1 text-xs rounded-full bg-${statusColor}-100 text-${statusColor}-800 capitalize`}>
                          {status}
                        </span>
                      </td>
                    </tr>
                  );
                })}
            </tbody>
          </table>
        </div>
      </div>

      {/* Today's Attendance */}
      <div className="bg-white shadow rounded-lg">
        <div className="px-4 py-4 border-b border-gray-200">
          <h3 className="text-lg font-medium text-gray-900">Today's Attendance</h3>
        </div>
        <div className="overflow-x-auto">
          <table className="min-w-full text-sm divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                {['Employee', 'Check In', 'Check Out', 'Status'].map((col) => (
                  <th key={col} className="px-4 py-2 text-left text-gray-500 font-medium uppercase">{col}</th>
                ))}
              </tr>
            </thead>
            <tbody className="divide-y bg-white">
              <tr>
                <td className="px-4 py-3">
                  <div className="flex items-center space-x-3">
                    <div className="h-8 w-8 rounded-full bg-gray-300 flex items-center justify-center text-sm font-medium text-gray-700">N</div>
                    <div>
                      <div className="font-medium text-gray-900">Nikhil Pant</div>
                      <div className="text-xs text-gray-500">NK2710IN</div>
                    </div>
                  </div>
                </td>
                <td className="px-4 py-3">09:00 AM</td>
                <td className="px-4 py-3">06:00 PM</td>
                <td className="px-4 py-3">
                  <span className="inline-flex px-2 py-1 text-xs font-semibold rounded-full bg-green-100 text-green-800">
                    Present
                  </span>
                </td>
              </tr>
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default Attendance;

// ✅ Reusable StatCard component (within same file or separate)
const StatCard = ({ icon: Icon, label, value, color }: { icon: any, label: string, value: any, color: string }) => (
  <div className="bg-white p-4 sm:p-6 rounded-lg shadow flex items-center">
    <Icon className={`h-8 w-8 text-${color}-500`} />
    <div className="ml-4">
      <p className="text-sm text-gray-500">{label}</p>
      <p className="text-2xl font-semibold text-gray-900">{value}</p>
    </div>
  </div>
);
