import React, { useState } from 'react';
import { FolderOpen, Upload, Search, Download, Trash2 } from 'lucide-react';

const Documents: React.FC = () => {
  const [passport, setPassport] = useState<File | null>(null);
  const [aadhar, setAadhar] = useState<File | null>(null);
  const [pan, setPan] = useState<File | null>(null);
  const [bank, setBank] = useState<File | null>(null);
  const [message, setMessage] = useState('');
  const [isUploading, setIsUploading] = useState(false);

  const empId = JSON.parse(localStorage.getItem('user') || '{}')?.employeeId;

  const handleUpload = async () => {
    if (!empId || !passport || !aadhar || !pan || !bank) {
      setMessage('❌ All documents and login are required.');
      return;
    }

    const formData = new FormData();
    formData.append('empId', empId);
    formData.append('passport', passport);
    formData.append('aadhar', aadhar);
    formData.append('pan', pan);
    formData.append('bank', bank);

    try {
      setIsUploading(true);
      setMessage('');
      const response = await fetch('http://leadinfo.site/employee/upload_documents.php', {
        method: 'POST',
        body: formData,
      });

      const result = await response.json();

      if (result.success) {
        setMessage('✅ Documents uploaded successfully!');
        setPassport(null);
        setAadhar(null);
        setPan(null);
        setBank(null);
      } else {
        setMessage(`❌ Upload failed: ${result.message}`);
      }
    } catch (error) {
      setMessage('❌ Network error or server unavailable.');
    } finally {
      setIsUploading(false);
    }
  };

  const renderCard = (
    title: string,
    inputId: string,
    file: File | null,
    setter: React.Dispatch<React.SetStateAction<File | null>>
  ) => (
    <div className="bg-white p-6 rounded-lg shadow hover:shadow-md transition-shadow">
      <div className="flex items-center justify-between mb-3">
        <div>
          <h3 className="text-lg font-semibold text-gray-900">{title}</h3>
          <p className="text-sm text-gray-500">Upload a valid document file</p>
        </div>
        <label
          htmlFor={inputId}
          className="cursor-pointer bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700 transition-colors flex items-center"
        >
          <Upload className="h-4 w-4 mr-2" />
          Browse
        </label>
        <input
          type="file"
          id={inputId}
          className="hidden"
          onChange={(e) => setter(e.target.files?.[0] || null)}
        />
      </div>
      <p className="text-sm text-gray-700 mt-1">
        {file?.name || 'No file selected'}
      </p>
    </div>
  );

  return (
    <div className="space-y-6">
      {/* Page Header */}
      <div>
        <h1 className="text-2xl font-bold text-gray-900">Documents</h1>
        <p className="mt-1 text-sm text-gray-500">
          Manage and organize your documents and files.
        </p>
      </div>

      {/* Upload Section - 4 Document Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {renderCard('Passport Size Photo', 'passport', passport, setPassport)}
        {renderCard('Aadhar Card', 'aadhar', aadhar, setAadhar)}
        {renderCard('PAN Card', 'pan', pan, setPan)}
        {renderCard('Bank Details', 'bank', bank, setBank)}
      </div>

      <div className="text-center mt-4">
        <button
          onClick={handleUpload}
          disabled={isUploading}
          className="bg-green-600 hover:bg-green-700 text-white px-6 py-2 rounded-md font-semibold transition disabled:opacity-50"
        >
          <Upload className="inline w-4 h-4 mr-2" />
          {isUploading ? 'Uploading...' : 'Submit Documents'}
        </button>
        {message && <p className="mt-3 text-sm text-gray-700">{message}</p>}
      </div>

      {/* Search and Filters */}
      {/* <div className="flex flex-col sm:flex-row gap-4">
        <div className="flex-1 relative">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-gray-400" />
          <input
            type="text"
            placeholder="Search documents..."
            className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
          />
        </div>
        <select className="px-4 py-2 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500">
          <option>All Types</option>
          <option>PDF</option>
          <option>Word</option>
          <option>Excel</option>
          <option>Images</option>
        </select>
      </div> */}

      {/* Documents Grid */}
      {/* <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        <div className="bg-white p-4 rounded-lg shadow hover:shadow-md transition-shadow">
          <div className="flex items-center justify-between mb-3">
            <FolderOpen className="h-8 w-8 text-blue-500" />
            <div className="flex space-x-2">
              <button className="text-gray-400 hover:text-blue-600">
                <Download className="h-4 w-4" />
              </button>
              <button className="text-gray-400 hover:text-red-600">
                <Trash2 className="h-4 w-4" />
              </button>
            </div>
          </div>
          <h4 className="font-medium text-gray-900 mb-1">Project Proposal.pdf</h4>
          <p className="text-sm text-gray-500 mb-2">2.5 MB • Updated 2 days ago</p>
          <div className="flex items-center text-xs text-gray-400">
            <span>Uploaded by: Nikhil Pant</span>
          </div>
        </div>

        <div className="bg-white p-4 rounded-lg shadow hover:shadow-md transition-shadow">
          <div className="flex items-center justify-between mb-3">
            <FolderOpen className="h-8 w-8 text-green-500" />
            <div className="flex space-x-2">
              <button className="text-gray-400 hover:text-blue-600">
                <Download className="h-4 w-4" />
              </button>
              <button className="text-gray-400 hover:text-red-600">
                <Trash2 className="h-4 w-4" />
              </button>
            </div>
          </div>
          <h4 className="font-medium text-gray-900 mb-1">Meeting Notes.docx</h4>
          <p className="text-sm text-gray-500 mb-2">1.2 MB • Updated 1 week ago</p>
          <div className="flex items-center text-xs text-gray-400">
            <span>Uploaded by: Nikhil Pant</span>
          </div>
        </div>

        <div className="bg-white p-4 rounded-lg shadow hover:shadow-md transition-shadow">
          <div className="flex items-center justify-between mb-3">
            <FolderOpen className="h-8 w-8 text-purple-500" />
            <div className="flex space-x-2">
              <button className="text-gray-400 hover:text-blue-600">
                <Download className="h-4 w-4" />
              </button>
              <button className="text-gray-400 hover:text-red-600">
                <Trash2 className="h-4 w-4" />
              </button>
            </div>
          </div>
          <h4 className="font-medium text-gray-900 mb-1">Budget Report.xlsx</h4>
          <p className="text-sm text-gray-500 mb-2">3.1 MB • Updated 3 days ago</p>
          <div className="flex items-center text-xs text-gray-400">
            <span>Uploaded by: Nikhil Pant</span>
          </div>
        </div>
      </div> */}

      {/* Recent Activity */}
      <div className="bg-white shadow rounded-lg">
        <div className="px-6 py-4 border-b border-gray-200">
          <h3 className="text-lg font-medium text-gray-900">Recent Activity</h3>
        </div>
        <div className="p-6">
          <div className="space-y-4">
            <div className="flex items-center space-x-3">
              <div className="h-8 w-8 bg-blue-100 rounded-full flex items-center justify-center">
                <Upload className="h-4 w-4 text-blue-600" />
              </div>
              <div>
                <p className="text-sm font-medium text-gray-900">New document uploaded</p>
                <p className="text-xs text-gray-500">Project Proposal.pdf • 2 hours ago</p>
              </div>
            </div>
            <div className="flex items-center space-x-3">
              <div className="h-8 w-8 bg-green-100 rounded-full flex items-center justify-center">
                <Download className="h-4 w-4 text-green-600" />
              </div>
              <div>
                <p className="text-sm font-medium text-gray-900">Document downloaded</p>
                <p className="text-xs text-gray-500">Meeting Notes.docx • 1 day ago</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Documents;
