import React, { useState } from 'react';
import { Upload } from 'lucide-react';

const UploadDocuments: React.FC = () => {
  const [passport, setPassport] = useState<File | null>(null);
  const [aadhar, setAadhar] = useState<File | null>(null);
  const [pan, setPan] = useState<File | null>(null);
  const [bank, setBank] = useState<File | null>(null);
  const [message, setMessage] = useState('');
  const [isUploading, setIsUploading] = useState(false);

  const empId = JSON.parse(localStorage.getItem('user') || '{}')?.employeeId;

  const handleUpload = async () => {
    if (!empId || !passport || !aadhar || !pan || !bank) {
      setMessage('❌ All documents and login are required.');
      return;
    }

    const formData = new FormData();
    formData.append('empId', empId);
    formData.append('passport', passport);
    formData.append('aadhar', aadhar);
    formData.append('pan', pan);
    formData.append('bank', bank);

    try {
      setIsUploading(true);
      setMessage('');
      const response = await fetch('http://leadinfo.site/employee/upload_documents.php', {
        method: 'POST',
        body: formData,
      });

      const result = await response.json();

      if (result.success) {
        setMessage('✅ Documents uploaded successfully!');
        setPassport(null);
        setAadhar(null);
        setPan(null);
        setBank(null);
      } else {
        setMessage(`❌ Upload failed: ${result.message}`);
      }
    } catch (error) {
      setMessage('❌ Network error or server unavailable.');
    } finally {
      setIsUploading(false);
    }
  };

  return (
    <div className="max-w-2xl mx-auto bg-white p-6 rounded-lg shadow space-y-6">
      <h2 className="text-xl font-bold text-gray-800">Upload Documents</h2>

      <div className="grid gap-4">
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">Passport Size Photo</label>
          <input type="file" onChange={(e) => setPassport(e.target.files?.[0] || null)} />
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">Aadhar Card</label>
          <input type="file" onChange={(e) => setAadhar(e.target.files?.[0] || null)} />
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">PAN Card</label>
          <input type="file" onChange={(e) => setPan(e.target.files?.[0] || null)} />
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">Bank Details</label>
          <input type="file" onChange={(e) => setBank(e.target.files?.[0] || null)} />
        </div>
      </div>

      <button
        onClick={handleUpload}
        disabled={isUploading}
        className="bg-blue-600 hover:bg-blue-700 text-white font-medium px-6 py-2 rounded-md transition disabled:opacity-50"
      >
        <Upload className="inline w-4 h-4 mr-2" />
        {isUploading ? 'Uploading...' : 'Upload Documents'}
      </button>

      {message && (
        <p className="text-sm mt-2 text-center text-gray-700">{message}</p>
      )}
    </div>
  );
};

export default UploadDocuments;
